import {Component, OnInit} from '@angular/core';
import {CategoriesService} from "../services/categories.service";

@Component({
  selector: 'app-categories',
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.css']
})
export class CategoriesComponent implements OnInit {

  // @ts-ignore
  categoryArray: { id: string, data: { category: string } }[];

  // @ts-ignore
  formCategory: string;

  // @ts-ignore
  formStatus: string = 'Add';

  constructor(private cs: CategoriesService) {
  }

  onSubmit(formData: any) {
    if(this.formStatus == 'Add')
    this.cs.saveData(formData);
    formData.reset();
  }

  onEdit(category: any) {
    this.formStatus = 'Edit'
    this.formCategory = category;

  }


  ngOnInit() {
    this.cs.loadData().subscribe(val => {
      let filtered: any = [];

      for (const valElement of val) {
        // @ts-ignore
        if (valElement.data.category) {
          filtered.push(valElement);
        }

      }

      this.categoryArray = filtered;
    });
  }


}
<div class="container">
  <div class="row">

    <div class="col-md-12 bg-secondary-theme p-5 shadow-effect">
      <h3 class="text-center">{{formStatus}} Categories</h3>
      <p class="text-center mb-5">You can {{formStatus}} new categories here</p>

      <form class="form-inline" #categoryForm = 'ngForm'  (ngSubmit)="onSubmit(categoryForm)">
        <div class="col-md-10">
          <input
            type="text"
            name="category"
            class="form-control shadow-effect"
            placeholder="Add new Categories"
            #newCategory = 'ngModel'
            [(ngModel)]="formCategory"
            required
            [ngClass]="{'is-invalid': newCategory.touched && newCategory.invalid}"
          >

          <div class="alert alert-danger" *ngIf="newCategory.touched && newCategory.invalid">
            Category field is required
          </div>
        </div>
        <div class="col-md-2">
          <button class="btn btn-blog btn-info mb-2 btn-theme" [disabled]="categoryForm.invalid">{{formStatus}} Category</button>
        </div>
      </form>
    </div>

  </div>

  <div class="row mt-5 mb-5">

    <div class="col-md-3"></div>

    <div class="col-md-6">
      <div class="card shadow-effect">
        <div class="card-body">
          <table class="table row-border hover">
            <thead>
              <tr>
                <th>No</th>
                <th>Category</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let category of categoryArray; let i = index">
                <td>{{i + 1}}</td>
                <td>{{category.data.category}}</td>
                <td><button class="btn btn-sm btn-warning" (click)="onEdit(category.data.category)">Edit</button></td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>

    <div class="col-md-3"></div>

  </div>

</div>
