import { Component } from '@angular/core';
import {AngularFirestore  } from "@angular/fire/compat/firestore";

@Component({
  selector: 'app-categories',
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.css']
})
export class CategoriesComponent {

  constructor(private afs: AngularFirestore) {
  }


  onSubmit(formData: any) {
    let categoryData = {
      category: formData.value.category
    }
    let subCategoryData = {
      subCategory: 'subCategory1'
    }
    this.afs.collection('categories').add(categoryData).then(docRef => {
      console.log(docRef);

      this.afs.collection('categories')
        .doc(docRef.id).collection('subcategories').add(subCategoryData)
        .then(docRef2 => {
          console.log(docRef2)}).catch(error => {
        console.log(error)})
      ;
    }).catch(err => {
      console.log(err)})

  }
}
<div class="container">
  <div class="row">

    <div class="col-md-12 bg-secondary-theme p-5 shadow-effect">
      <h3 class="text-center">New Categories</h3>
      <p class="text-center mb-5">You can add new categories here</p>

      <form class="form-inline" #categoryForm = 'ngForm'  (ngSubmit)="onSubmit(categoryForm)">
        <div class="col-md-10">
          <input
            type="text"
            name="category"
            class="form-control shadow-effect"
            placeholder="Add new Categories"
            ngModel

          >
        </div>
        <div class="col-md-2">
          <button class="btn btn-blog btn-info mb-2 btn-theme">Add Category</button>
        </div>
      </form>
    </div>

  </div>
</div>
