import {Component, OnInit} from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit{

  // @ts-ignore
  currentUserEmail: string;

  constructor() {
  }

  ngOnInit() {
    let user = localStorage.getItem("user")!;
    this.currentUserEmail = JSON.parse(user).email;
  }

}
<header class=" navbar navbar-expand-lg fixed-top bg-white ">

  <div class="container justify-content-center">
    <a href="#" class="site-logo"> ANG-BLOG </a>
  </div>
  <div class="container justify-content-center">
    <a href="#" class="mr-3">{{currentUserEmail}}</a>
    <button class="btn btn-sm btn-info bg-secondary-theme text-theme-primary">Logout</button>
  </div>

</header>
